<?php/** * @author webrek * @copyright 2013 */include "lib/config.php";class contenido{    var $op;    var $html = '';    function __construct()    {        $this->op = $_REQUEST['op'];        switch ($this->op) {            default:            $this->html .= $this->loadFromHtml('home.html');                break;            case 'contacto':                $this->html .= $this->loadFromHtml('contacto.html');                break;            case '404':                $this->html .= $this->loadFromHtml('404.html');                break;        }        return $this->html;    }        function loadFromDb($nombre){        $sql = "SELECT * FROM contenido WHERE nombre = '$nombre'";        $query = mysql_query($sql) or die (mysql_error());        $array = mysql_fetch_array($query);        return $array['html'];    }    function loadFromHtml($file){        global $config;        if(file_exists("templates/".$file)){            $html = file_get_contents("templates/".$file);            return $html;        }    }}?>